function check_forbidden_cities(city_name, country_code) {
    var forbidd_list = forbidden_cities_in_countries[country_code];
    if (forbidd_list) {
        for (var i = 0; i < forbidd_list.length; i++) {
            if (forbidd_list[i].toLowerCase() === "" + city_name) {
                return true;
            }
        }
    }
    return false;
}


