var options = {};

try {
    // console.log(msg.city);
    var payload = JSON.parse(msg.payload);
    // msg.payload = JSON.parse(msg.payload);
    if (payload.status === 404) {
        return [null, msg];
    }
    // console.log(payload);
    var url = payload._links.self.href;

    var start = url.indexOf("slug:") + 5; // Get the index of "slug:" and add 5 to skip over it
    var end = url.indexOf("/", start); // Get the index of the next "/" after "slug:"
    var citySlug = url.substring(start, end);

    //console.log(citySlug);

    options.city_name = citySlug;
    //options.city_summary = payload.summary.replace(/(<([^>]+)>)/gi, "").trim();
    options.city_summary = payload.summary  // Remove HTML tags using regex
        .replace(/<\/?[^>]+(>|$)/g, "")
        // Replace newline characters with empty string
        .replace(/\n/g, "")
        .replace(/['"]/g, "")
        // Trim any remaining whitespace from beginning and end
        .trim();


    for (var i = 0; i < payload.categories.length; i++) {
        var categ = payload.categories[i];
        var key = categ.name.replace(/[\s&]+/g, '_').replace(/_{2,}/g, '_');
        options[key.toLowerCase()] = categ.score_out_of_10;
    }

    var city_saved = msg.city;
    options.lat = city_saved.lat;
    options.lng = city_saved.lng;
    options.geonameId = city_saved.geonameId;
    options.country_code = city_saved.country_code;
    options.continent_code = city_saved.continent_code;
    msg.payload = options;
    return [msg, null];
} catch (error) {
    // does nothing
    return [null, msg];
}

