function calc_weight_avg(list) {
    // Calculate total weight and weighted sum
    let totalWeight = 0;
    let weightedSum = 0;

    list.forEach((temperature, index) => {
        const weight = index + 1;

        weightedSum += temperature.value * weight;
        totalWeight += weight;
    });

    const weightedAverage = weightedSum / totalWeight;
    return weightedAverage;
}

const list = msg.payload.data;
for (let key in list) {
    list[key] = calc_weight_avg(list[key]);
}

msg.payload.data = list;
return msg;
