        var obj = {};
        obj.time = payload.daily.time[i];
        obj.weathercode = payload.daily.weathercode[i];
        obj.temperature = parseFloat((payload.daily.temperature_2m_max[i] + payload.daily.temperature_2m_min[i]) / 2).toFixed(2);
        obj.uv_index_max = payload.daily.uv_index_max[i];
        obj.uv_index_clear_sky_max = payload.daily.uv_index_clear_sky_max[i];
        obj.rain_sum = payload.daily.rain_sum[i];
        obj.snowfall_sum = payload.daily.snowfall_sum[i];
        obj.windspeed_10m_max = payload.daily.windspeed_10m_max[i];
        obj.winddirection_10m_dominant = payload.daily.winddirection_10m_dominant[i];
        obj.shortwave_radiation_sum = payload.daily.shortwave_radiation_sum[i];
