        obj.city_lat = msg.city_lat;
        obj.city_lng = msg.city_lng;
        obj.geonameId = msg.geonameId;
        //console.log(obj.city_name);
        options.push(obj);
    }

    msg.payload = options;
    return [msg, null];
} catch (error) {
    // does nothing
    return [null, msg];
}

